import React from 'react';

var styles = {
	color:'red',
	backgroundColor:'black',
    fontWeight:'bold',
    maxwidth: 20
};

const LaunchItem= ({ launch: {flight_number, mission_name, launch_year, launch_date_local, launch_success}}) => (
    <div className="container">
        <div className="card text-white bg-success mb-3" style={styles}>
            <div className="card-header">Mission: { mission_name }</div>
            <div className="card-header row">
                <div className="col-md-9">
                    <h5 className="card-title">Date: { launch_date_local} </h5>
                </div>
                <div className="col-md-3">
                    <button className="btn btn-secondary">Launch Details</button>
                </div>
            </div>
        </div>
    </div>

)

export default LaunchItem;
